Create View [dbo].[V_AllHomeWorks] as
select h.* from HomeWork h
inner Join CategoryRelated cr on h.HwId=cr.ChildId
where cr.ParentId in (select CategoryId from(
select cr.ParentId as CategoryId,cr.ChildId TopicId,sr.ParentId as SectionId,ROW_NUMBER()over(Partition by cr.ParentId order by sr.childid desc) as Num from CategoryRelated cr
left join SectionRelated sr on cr.ChildId=sr.ChildId) as t
where t.Num=1 and t.SectionId is Null)
Union all
select h.* from HomeWork h
inner Join CategoryRelated cr on h.HwId=cr.ChildId
inner Join SectionRelated sr on  sr.ChildId=h.HwId
where cr.ParentId in (select CategoryId from(
select cr.ParentId as CategoryId,cr.ChildId TopicId,sr.ParentId as SectionId,ROW_NUMBER()over(Partition by cr.ParentId order by sr.childid desc) as Num from CategoryRelated cr
left join SectionRelated sr on cr.ChildId=sr.ChildId) as t
where t.Num=1 and t.SectionId is not Null)
GO

