/**************** 6、新建视图 [createAllUserProfileViewLabel]，并执行、添加用户权限 *********************/
/****** Object:  StoredProcedure [dbo].[createAllUserProfileViewLabel]    Script Date: 01/09/2015 10:42:53 ******/
SET ANSI_NULLS ON
GO

SET QUOTED_IDENTIFIER ON
GO
 CREATE proc [dbo].[createAllUserProfileViewLabel]

as
begin
	declare @sql varchar(max),
	@columns varchar(2000)
	select @columns=isnull(@columns+',','')+ Code from [Profile] where Bak2 is null or Bak2<>'-1'
	
	if exists(select * from sysobjects where name='view_allUserProfileLabel')
	  drop view view_allUserProfileLabel;
	  
	 set @sql='
	 create view view_allUserProfileLabel
	as
	select u.*,r.*
	from(select up.UserPassportId as uid,p.Code,ISNULL(pe.Label,up.Value) Label from UserProfile up
		 left join Profile p on up.ProfileId = p.ProfileId left join ProfileEnum pe on up.ProfileId=pe.ProfileId and up.Value=CONVERT(CHAR(23), pe.value, 21)) as upro
	Pivot(
		max(upro.Label) for upro.Code IN
		('+@columns+')) as r
		right join UserPassport u on r.uid=u.UserPassportId
		'
		exec (@sql)
	
end
GO

